import React, { useState, useEffect } from "react";
import "./App.css";
import ChatWindow from "./components/ChatWindow";
import InputForm from "./components/InputForm";

function App() {
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(false);
  const [currentQuestion, setCurrentQuestion] = useState("");

  // Fetch the first question when the component loads
  useEffect(() => {
    getQuestion(0);
  }, []);

  const getQuestion = async (index) => {
    try {
      const response = await fetch(
        `http://localhost:3001/chat/question/${index}`
      );
      const data = await response.json();
      setCurrentQuestion(data.question);
    } catch (error) {
      console.error("Error fetching question:", error);
    }
  };

  const submitAnswer = async (input) => {
    setMessages([...messages, { user: "You", text: input }]);
    setLoading(true);

    try {
      const response = await fetch("http://localhost:3001/chat/answer", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          userId: "12345",
          questionIndex: messages.length,
          answer: input,
        }),
      });

      const data = await response.json();
      setMessages([
        ...messages,
        { user: "You", text: input },
        { user: "Chatbot", text: data.aiResponse },
      ]);
    } catch (error) {
      console.error("Error submitting answer:", error);
    }

    setLoading(false);
  };

  return (
    <div className="chat-container">
      <h1>Chatbot</h1>
      <ChatWindow messages={messages} />
      <InputForm onSubmit={submitAnswer} loading={loading} />
    </div>
  );
}

export default App;
