import React, { useState, useEffect } from "react";
import "./App.css";
import ChatWindow from "./components/ChatWindow";
import InputForm from "./components/InputForm";
import { v4 as uuidv4 } from "uuid"; // For generating unique session ID

function App() {
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(false);
  const [isTyping, setIsTyping] = useState(false);
  const [sessionId, setSessionId] = useState(null); // Session ID
  const [sessionStartTime, setSessionStartTime] = useState(null); // Start timestamp
  const [questionCount, setQuestionCount] = useState(0); // Track how many questions asked

  // Automatically start session when component mounts
  useEffect(() => {
    startSession();
  }, []);

  const startSession = () => {
    const newSessionId = uuidv4(); // Generate unique session ID
    setSessionId(newSessionId);
    setSessionStartTime(new Date().toISOString());
    getNextQuestion(); // Ask the first question
  };

  const getNextQuestion = async () => {
    if (questionCount < 10) {
      setIsTyping(true);

      try {
        // Fetch a random question from OpenAI
        const response = await fetch(
          "http://localhost:3001/chat/randomQuestion",
          {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
            },
            body: JSON.stringify({
              sessionId,
              sessionStartTime,
              questionCount,
            }),
          }
        );

        const data = await response.json();
        const newQuestion = data.question;
        setMessages((prevMessages) => [
          ...prevMessages,
          { user: "Chatbot", text: newQuestion },
        ]);
        setQuestionCount(questionCount + 1); // Increment the question count
        setIsTyping(false);
      } catch (error) {
        console.error("Error fetching random question:", error);
      }
    } else {
      // End the session after 10 questions
      console.log("All 10 questions asked, ending session.");
    }
  };

  const submitAnswer = async (input) => {
    setMessages((prevMessages) => [
      ...prevMessages,
      { user: "You", text: input },
    ]); // Show user answer in chat
    setLoading(true);
    setIsTyping(true);

    try {
      const response = await fetch("http://localhost:3001/chat/answer", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          sessionId,
          questionIndex: questionCount,
          answer: input,
          sessionStartTime,
        }),
      });

      const data = await response.json();
      // Keep the answer visible and proceed to the next question
      getNextQuestion();
    } catch (error) {
      console.error("Error submitting answer:", error);
    }

    setLoading(false);
    setIsTyping(false);
  };

  return (
    <div className="chat-container">
      <h1>Chatbot</h1>
      <ChatWindow messages={messages} isTyping={isTyping} />
      <InputForm onSubmit={submitAnswer} loading={loading} />
    </div>
  );
}

export default App;
