import React, { useState, useEffect } from "react";
import "./App.css";
import ChatWindow from "./components/ChatWindow";
import InputForm from "./components/InputForm";
import { v4 as uuidv4 } from "uuid"; // For generating unique session ID

function App() {
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(false);
  const [isTyping, setIsTyping] = useState(false);
  const [sessionStarted, setSessionStarted] = useState(false); // For session handling
  const [sessionId, setSessionId] = useState(null); // Session ID
  const [sessionStartTime, setSessionStartTime] = useState(null); // Start timestamp

  useEffect(() => {
    // Fetch the first question when the session starts
    if (sessionStarted) {
      getQuestion(0);
    }
  }, [sessionStarted]);

  const startSession = () => {
    const newSessionId = uuidv4(); // Generate unique session ID
    setSessionId(newSessionId);
    setSessionStartTime(new Date().toISOString()); // Record the session start timestamp
    setSessionStarted(true); // Start the session
  };

  const getQuestion = async (index) => {
    try {
      const response = await fetch(
        `http://localhost:3001/chat/question/${index}`
      );
      const data = await response.json();
      setMessages([...messages, { user: "Chatbot", text: data.question }]);
    } catch (error) {
      console.error("Error fetching question:", error);
    }
  };

  const submitAnswer = async (input) => {
    setMessages([...messages, { user: "You", text: input }]);
    setLoading(true);
    setIsTyping(true);

    try {
      const response = await fetch("http://localhost:3001/chat/answer", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          sessionId, // Include the session ID
          questionIndex: messages.length,
          answer: input,
          sessionStartTime,
        }),
      });

      const data = await response.json();
      setMessages([
        ...messages,
        { user: "You", text: input },
        { user: "Chatbot", text: data.aiResponse },
      ]);
    } catch (error) {
      console.error("Error submitting answer:", error);
    }

    setLoading(false);
    setIsTyping(false);
  };

  return (
    <div className={`chat-container ${sessionStarted ? "" : "disabled-chat"}`}>
      {!sessionStarted && (
        <div className="start-session-overlay">
          <button onClick={startSession} className="start-session-btn">
            Start Session
          </button>
        </div>
      )}
      {sessionStarted && (
        <>
          <h1>Chatbot</h1>
          <ChatWindow messages={messages} isTyping={isTyping} />
          <InputForm onSubmit={submitAnswer} loading={loading} />
        </>
      )}
    </div>
  );
}

export default App;
