const ChatMessage = ({ question, answer }) => {
  return (
    <div className="chat-message">
      <p>
        <strong>Q:</strong> {question}
      </p>
      <p>
        <strong>A:</strong> {answer}
      </p>
    </div>
  );
};

export default ChatMessage;
