import React, { useState, useRef, useEffect } from "react";
import "./InputForm.css";

function InputForm({ onSubmit, loading }) {
  const [input, setInput] = useState("");
  const inputRef = useRef(null); // Create a ref for the input element

  // Automatically focus the input field when the component mounts
  useEffect(() => {
    if (inputRef.current) {
      inputRef.current.focus(); // Set focus on the input field on mount
    }
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!input) return;

    onSubmit(input); // Submit the answer
    setInput(""); // Clear the input field after submission

    // Re-focus the input after the form is submitted
    inputRef.current.focus(); // Immediately refocus the input after submission
  };

  return (
    <form onSubmit={handleSubmit} className="chat-input-form">
      <input
        type="text"
        value={input}
        onChange={(e) => setInput(e.target.value)}
        placeholder={loading ? "Loading..." : "Type your message..."}
        disabled={loading}
        ref={inputRef} // Attach the ref to the input element
        autoFocus // Ensure that the input is auto-focused when the component mounts
      />
      <button type="submit" disabled={loading}>
        {loading ? "..." : "Send"}
      </button>
    </form>
  );
}

export default InputForm;
