import React from "react";
import MessageCard from "./MessageCard";
import "./ChatWindow.css";

function ChatWindow({ messages, isTyping, hasFinished, startNewSession }) {
  return (
    <div className="chat-window">
      {messages.map((msg, index) => (
        <MessageCard key={index} user={msg.user} text={msg.text} />
      ))}

      {isTyping && <div className="typing-indicator">Chatbot is typing...</div>}

      {/*closing statement*/}
      {hasFinished && (
        <div className="closing-message">
          <p>
            Thank you for your contribution, your answers will be evaluated
            quickly.
          </p>
          <p>To start a new session, please feel free to continue.</p>
          <button className="start-session-button" onClick={startNewSession}>
            Start New Session
          </button>
        </div>
      )}
    </div>
  );
}

export default ChatWindow;
