import React, { useState, useRef, useEffect } from "react";
import "./InputForm.css";

function InputForm({ onSubmit, loading }) {
  const [input, setInput] = useState("");
  const inputRef = useRef(null); // Create a ref for the input element

  // Automatically focus the input field when the component mounts and after every render
  useEffect(() => {
    if (inputRef.current) {
      console.log("Ensuring input field is focused");
      inputRef.current.focus(); // Keep the input field focused after every render
    }
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!input) return;

    console.log("Form submitted:", input); // Log the submitted value

    onSubmit(input); // Submit the user's answer
    setInput(""); // Clear the input field

    // Re-focus the input field after submission
    setTimeout(() => {
      if (inputRef.current) {
        console.log("Re-focusing input field:", inputRef.current); // Log the re-focus attempt
        inputRef.current.focus(); // Focus after input is cleared
      } else {
        console.error("Input field ref is null on re-focus");
      }
    }, 0);
  };

  return (
    <form onSubmit={handleSubmit} className="chat-input-form">
      <input
        type="text"
        value={input}
        onChange={(e) => setInput(e.target.value)}
        placeholder={loading ? "Loading..." : "Type your message..."}
        disabled={loading}
        ref={inputRef} // Attach the ref to the input element
        autoFocus
      />
      <button type="submit" disabled={loading}>
        {loading ? "..." : "Send"}
      </button>
    </form>
  );
}

export default InputForm;
