import React, { useState, useEffect, useRef } from "react";
import "./App.css";
import ChatWindow from "./components/ChatWindow";
import InputForm from "./components/InputForm";
import { v4 as uuidv4 } from "uuid";

function App() {
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(false);
  const [isTyping, setIsTyping] = useState(false);
  const [sessionId, setSessionId] = useState(null);
  const [sessionStartTime, setSessionStartTime] = useState(null);
  const [questionCount, setQuestionCount] = useState(0);
  const [sessionStarted, setSessionStarted] = useState(false);
  const [sessionEnded, setSessionEnded] = useState(false);
  const chatWindowRef = useRef(null); // Scroll to bottom ref

  // Scroll to the bottom when new messages are added
  useEffect(() => {
    if (chatWindowRef.current) {
      chatWindowRef.current.scrollTop = chatWindowRef.current.scrollHeight;
    }
  }, [messages]);

  useEffect(() => {
    if (!sessionStarted) {
      startSession(); // Only start a new session if it hasn't been started yet
      setSessionStarted(true);
    }
  }, [sessionStarted]);

  const startSession = () => {
    const newSessionId = uuidv4();
    const newSessionStartTime = new Date().toISOString();

    // Reset state for new session
    setSessionId(newSessionId);
    setSessionStartTime(newSessionStartTime);
    setQuestionCount(0);
    setMessages([]);
    setSessionEnded(false); // Reset session ended state
    setSessionStarted(true);
    getNextQuestion(newSessionId, newSessionStartTime); // Ask first question immediately
  };

  const getNextQuestion = async (sessionId, sessionStartTime) => {
    if (questionCount < 10) {
      setIsTyping(true);

      try {
        const response = await fetch(
          "http://localhost:3001/chat/randomQuestion",
          {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
            },
            body: JSON.stringify({
              sessionId,
              sessionStartTime,
              questionCount,
            }),
          }
        );

        const data = await response.json();
        setMessages((prevMessages) => [
          ...prevMessages,
          { user: "Chatbot", text: data.question },
        ]);
        setQuestionCount((prevCount) => prevCount + 1);
        setIsTyping(false);

        // End session after 10 questions
        if (questionCount + 1 >= 10) {
          setSessionEnded(true);
        }
      } catch (error) {
        console.error("Error fetching random question:", error);
        setIsTyping(false);
      }
    }
  };

  const submitAnswer = async (input) => {
    setMessages((prevMessages) => [
      ...prevMessages,
      { user: "You", text: input },
    ]);
    setLoading(true);
    setIsTyping(true);

    try {
      const response = await fetch("http://localhost:3001/chat/answer", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          sessionId,
          questionIndex: questionCount - 1, // Adjust for zero-based index
          answer: input,
          sessionStartTime,
        }),
      });

      await response.json();

      if (questionCount + 1 < 10) {
        getNextQuestion(sessionId, sessionStartTime); // Fetch the next question
      }
    } catch (error) {
      console.error("Error submitting answer:", error);
    }

    setLoading(false);
    setIsTyping(false);
  };

  return (
    <div className="chat-container">
      <h1>Bolt Chat</h1>
      <div ref={chatWindowRef}>
        <ChatWindow messages={messages} isTyping={isTyping} />
      </div>
      {!sessionEnded && <InputForm onSubmit={submitAnswer} loading={loading} />}

      {sessionEnded && (
        <div className="closing-message">
          <p>
            Thank you for your contribution! Your answers will be evaluated
            quickly.
          </p>
          <p>To start a new session, please feel free to continue.</p>
          <button className="start-session-button" onClick={startSession}>
            Start New Session
          </button>
        </div>
      )}
    </div>
  );
}

export default App;
